import React, { useState, useContext, useEffect } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import { TokenContext } from "./TokenContext";
import "../styles/header.css";

import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faUtensils,
  faStar,
  faUserEdit,
  faSignOutAlt,
  faAngleUp,
  faAngleDown,
} from "@fortawesome/free-solid-svg-icons";
import {
  Collapse,
  Navbar,
  NavbarToggler,
  NavbarBrand,
  Nav,
  NavItem,
  NavLink,
} from "reactstrap";
import SearchBar from "./SearchBar";
import Profile from "./Profile";
// import { useHistory, Link } from "react-router-dom";
import { Link } from "react-router-dom";

const Header = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [token, setToken] = useContext(TokenContext);
  const [show, setShow] = useState(false);
  // const history=useHistory();

  const handleSignOut = () => {
    setToken(null);
    localStorage.removeItem("token");
  };
  const handleShowInfo = () => {
    setShow(!show);
  };

  const toggle = () => setIsOpen(!isOpen);

  useEffect(() => {
    if (token) {
      fetch(
        `https://yummydb-api-hy2snorce.vercel.app
/check-token`,
        {
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
        }
      )
        .then((res) => res.json())
        .then((data) => {
          if (data === "expired") {
            setToken(null);
            localStorage.removeItem("token");
          }
        });
    }
  }, [setToken, token]);

  return (
    <div style={{ fontSize: "18px", position: "relative", zIndex: "3" }}>
      <Navbar
        style={{ padding: "8px 40px 8px 50px", backgroundColor: "#000" }}
        dark
        expand="md"
      >
        <NavbarBrand href="/">
          <span className="logoA">yummy </span>
          <span className="logoB">DB</span>
        </NavbarBrand>
        <NavbarToggler onClick={toggle} />
        <Collapse isOpen={isOpen} navbar>
          <Nav className="mr-auto, nav-bar" navbar>
            <NavItem style={{ paddingLeft: "40px" }}>
              <Link to="/recipes">
                <div className="navLinkBtn recipes-btn">
                  Recipes{" "}
                  <FontAwesomeIcon className="search-icon" icon={faUtensils} />
                </div>
              </Link>
            </NavItem>
            <NavItem>
              <SearchBar />
            </NavItem>
            {token ? (
              <div className="nav">
                <NavLink>
                  <div className="navLinkBtn" onClick={handleShowInfo}>
                    <span>{show ? "hide info" : "show info"}</span>{" "}
                    {show ? (
                      <FontAwesomeIcon
                        style={{ paddingTop: "4px" }}
                        icon={faAngleUp}
                      />
                    ) : (
                      <FontAwesomeIcon
                        style={{ paddingTop: "4px" }}
                        icon={faAngleDown}
                      />
                    )}
                  </div>
                </NavLink>
                <NavItem>
                  <Link title="your favorite recipes" to="/favorite">
                    <div className="navLinkBtn">
                      <FontAwesomeIcon icon={faStar} />
                    </div>
                  </Link>
                </NavItem>
                <NavItem>
                  <Link title="update your info" to="/edit-account-info">
                    <div className="navLinkBtn">
                      <FontAwesomeIcon icon={faUserEdit} />
                    </div>
                  </Link>
                </NavItem>
                <Link to="/">
                  <div
                    className="navLinkBtn"
                    title="sign out"
                    onClick={handleSignOut}
                  >
                    <FontAwesomeIcon icon={faSignOutAlt} />
                  </div>
                </Link>
              </div>
            ) : (
              <div
                style={{
                  display: "flex",
                  justifyContent: "space-between",
                  width: "150px",
                }}
              >
                <NavItem>
                  <Link to="/sign-up">
                    <div className="navLinkBtn">Sign up</div>
                  </Link>
                </NavItem>
                <NavItem>
                  <Link to="/login">
                    <div className="navLinkBtn">Log in</div>
                  </Link>
                </NavItem>
              </div>
            )}
          </Nav>
        </Collapse>
      </Navbar>

      <Profile pos={show ? "open" : ""} />
    </div>
  );
};

export default Header;
