import React, { useState, useEffect } from "react";
import {Link} from 'react-r'

function Categories() {
  const [recipes, setRecipes] = useState([]);
  const [recipesB, setRecipesB]=useState([]);
  const key = "d21f98ccdf934ed5ac7c1e724093d571";

  useEffect(() => {
    getRecipes();
  }, []);

  const getRecipes = () => {
    fetch(
      `https://api.spoonacular.com/recipes/search?&type=Side Dish&offset=2&number=5&apiKey=${key} `,
      {
        method: "GET",
        "Content-Type": "application/json",
      }
    )
      .then((res) => res.json())
      .then((res) => setRecipes(res.results))
      .catch((err) => {
        console.log(err);
      });

        fetch(
          `https://api.spoonacular.com/recipes/search?&type=Dessert&offset=2&number=5&apiKey=${key} `,
          {
            method: "GET",
            "Content-Type": "application/json",
          }
        )
          .then((res) => res.json())
          .then((res) => setRecipesB(res.results))

          .catch((err) => {
            console.log(err);
          });

  };

  return (
    <div style={{ display: "flex", flexDirection: "column" }}>
      <div
        className="CourseTitle"
        style={{
          textAlign: "left",
          fontSize: "54px",
          backgroundColor: "#b526f2",
          fontWeight: "900",
          padding: "20px",
          color: "#fff",
          width: "50%",
          position: "relative",
        }}
      >
        <div
          style={{
            position: "absolute",
            right: "150px",
            bottom: "-40px",
            backgroundColor: "#fff",
            width: "20px",
            height: "200px",
            transform: "rotate(45deg)",
          }}
        ></div>
        Side Dish
      </div>
      <div className="recipes-section">
        {recipes.map((res) => (
          <Link key={res.id} className="card-image" to={`recipe/${res.id}`}>
            <div id="image">
              <img
                src={`https://spoonacular.com/recipeImages/${res.image}`}
                alt={res.title}
                width="100%"
              />
            </div>
            <h2 id="recipe-title">
              {res.title.replace(/^\w/, (c) => c.toUpperCase())}
            </h2>
            <div id="time-icon">
              <i className="far fa-clock"></i>
              <span id="time">
                {res.readyInMinutes > 60
                  ? Math.floor(res.readyInMinutes / 60) + "h"
                  : res.readyInMinutes + "min"}
              </span>
            </div>
          </Link>
        ))}
      </div>
      <h5
        style={{ display: "flex", justifyContent: "center", marginTop: "10px" }}
      >
        Show more
      </h5>

      <h5 style={{ marginLeft: "20px", marginTop: "10px" }}>category B</h5>
      <div className="recipes-section">
        {recipesB.map((res) => (
          <Link key={res.id} className="card-image" to={`recipe/${res.id}`}>
            <div id="image">
              <img
                src={`https://spoonacular.com/recipeImages/${res.image}`}
                alt={res.title}
                width="100%"
              />
            </div>
            <h2 id="recipe-title">
              {res.title.replace(/^\w/, (c) => c.toUpperCase())}
            </h2>
            <div id="time-icon">
              <i className="far fa-clock"></i>
              <span id="time">
                {res.readyInMinutes > 60
                  ? Math.floor(res.readyInMinutes / 60) + "h"
                  : res.readyInMinutes + "min"}
              </span>
            </div>
          </Link>
        ))}
      </div>
      <h5
        style={{ display: "flex", justifyContent: "center", marginTop: "10px" }}
      >
        Show more
      </h5>
    </div>
  );
}

export default Categories;

const Section = {
  display: "webkit-box",
  display: "ms-lexbox",
  display: "flex",
  msFlexWrap: "wrap",
  flexWrap: "wrap",
  msFlexPack: "distribute",
  justifyContent: "space-around",
  marginTop: "15px",
};

const card = {
  width: "200px",
  minHeight: "200px",
  lineHeight: "200px",
};

const cardImg = {
  width: "200px",
  height: "200px",
  lineHeight: "200px",
  fontWeight: "100",
  boxShadow: "2px 1px 10px 2px rgb(150, 144, 147)",
};

const title = {
  marginTop: "10px",
  textAlign: "center",
  fontFamily: "Arial, Helvetica, sans-serif",
  fontStyle: "italic",
};
