import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import "../styles/MainCourse.css";
function MainCourse() {
  const [recipes, setRecipes] = useState([]);
  const key = "d89d17872b3f4f8fa0da39073a9defdf";

  useEffect(() => {
    getRecipes();
  }, []);

  const getRecipes = () => {
    fetch(
      // `https://api.spoonacular.com/recipes/search?&type=Main Course&offset=2&number=5&apiKey=${key} `,
      {
        method: "GET",
        "Content-Type": "application/json",
      }
    )
      .then((res) => res.json())
      .then((res) => setRecipes(res.results))
      // .then(res => console.log(res.recipes))
      .catch((err) => {
        console.log(err);
      });
  };


  return (
    <div style={{display: 'flex', flexDirection: 'column',}}>
      <div
        className="MainCourseTitle"
        style={{
          textAlign: "left",
          fontSize: "54px",
          backgroundColor: "#F94F72",
          fontWeight: "900",
          padding: "20px",
          color: "#fff",
          width: "50%",
          position: "relative",
        }}
      >
        <div
          style={{
            position: "absolute",
            right: "150px",
            bottom: "-40px",
            backgroundColor: "#fff",
            width: "20px",
            height: "200px",
            transform: "rotate(45deg)",
          }}
        ></div>
        Main Course
      </div>
      <div style={Section}>
        {recipes.map((rec) => (
          <Link key={rec.id} to={`recipe/${rec.id}`} style={title}>
            <div
              style={card}
              key={rec.id}
              className="animationCard hvr-wobble-to-bottom-right"
            >
              <img
                className="card-image-all-food"
                width="100px"
                src={`https://spoonacular.com/recipeImages/${rec.image}`}
                alt={rec.title}
              />
              {rec.title.split(" ").slice(0, 4).join(" ")}
            </div>
          </Link>
        ))}
      </div>
      <div style={{ backgroundColor: "#F94F72", color: '#fff', padding: '10px', width: 'fit-content', alignSelf: 'right' }}>Show More ></div>
    </div>
  );
}
export default MainCourse;

const Section = {
  display: "webkit-box",
  display: "ms-lexbox",
  display: "flex",
  msFlexWrap: "wrap",
  flexWrap: "wrap",
  msFlexPack: "distribute",
  justifyContent: "space-around",
  marginTop: "15px",
  background: "#fff",
  backgroundSize: "cover",
};

const card = {
  width: "200px",
  minHeight: "200px",
  // lineHeight: '50px',
  borderRadius: "50%",
  MozBorderRadius: "50%",
  WebkitBorderRadius: "50%",
};

const title = {
  marginTop: "15px",
  textAlign: "center",
  fontFamily: "Arial, Helvetica, sans-serif",
  fontStyle: "italic",
};
