import React, { useState } from "react";
import { useHistory } from "react-router-dom";

export default function Info() {
    const [height, setHeight]= useState('');
    const [weight, setWeight] = useState('');
    const [age, setAge] = useState('');
    const [male, setMale]=useState(false);
    const [female, setFemale] = useState(false);
    const [ecto, setEcto]= useState(false);
    const [meso, setMeso]= useState(false);
    const [endo, setEndo]= useState(false);
    const [gain, setGain]= useState(false);
    const [lose, setLose]= useState(false);
    const [maintain, setMaintain]= useState(false);
    const [lowCarbs, setLowCarbs]=useState(false);
    const [moderateCarbs, setModerateCarbs]=useState(false);
    const [highCarbs, setHighCarbs]=useState(false);
    const [daysOfWorkout, setDaysOfWorkouts]=useState('0');
    const [durationOfWorkout, setDurationOfWorkout]=useState('0');

    const handleSubmit=(e)=>{
       e.preventDefault();
      console.log(male, female);
    }

    return (
      <div>
        <form
          onSubmit={handleSubmit}
          style={{ display: "flex", flexWrap: "wrap" }}
        >
          <div style={{width: '50%'}}>
            <h2>Body Type and Measurements</h2>
            <div>Height</div>
            <input
              type="number"
              onChange={e => setHeight(e.target.value)}
              value={height}
              placeholder="Height (cm)"
            />
            <div>Weight</div>
            <input
              type="number"
              onChange={e => setWeight(e.target.value)}
              value={weight}
              placeholder="Weight (kg)"
            />
            <div>Age</div>
            <input
              type="number"
              onChange={e => setAge(e.target.value)}
              value={age}
              placeholder="Age (year)"
            />
            <div>
              <label>
                <input
                  type="radio"
                  name="gender"
                  onChange={e => {
                    setMale(e.target.checked);
                    setFemale(!e.target.checked);
                  }}
                />
                male
              </label>

              <label>
                <input
                  type="radio"
                  name="gender"
                  onChange={e => {
                    setFemale(e.target.checked);
                    setMale(!e.target.checked);
                  }}
                />
                female
              </label>
            </div>
            <h3>Body Type:</h3>
            <label>
              <input
                type="radio"
                name="bodyType"
                onChange={e => {
                  setEcto(e.target.checked);
                  setEndo(!e.target.checked);
                  setMeso(!e.target.checked);
                }}
              />
              Ectomorph
            </label>
            <p>* struggles to gain weight</p>
            <label>
              <input
                type="radio"
                name="bodyType"
                onChange={e => {
                  setEcto(!e.target.checked);
                  setEndo(!e.target.checked);
                  setMeso(e.target.checked);
                }}
              />
              Mesomorph
            </label>
            <p>* easily gains and loses weight</p>
            <label>
              <input
                type="radio"
                name="bodyType"
                onChange={e => {
                  setEcto(!e.target.checked);
                  setEndo(e.target.checked);
                  setMeso(!e.target.checked);
                }}
              />
              Endomorph
            </label>
            <p>* easily gains weight, struggles to lose weight</p>
          </div>
          <div>
            <h2>Workout & Sport</h2>
            <div>
              Days of Workout per Week{" "}
              <input
                type="number"
                onChange={e => setDaysOfWorkouts(e.target.value)}
                value={daysOfWorkout}
              />
            </div>
            <div>
              Duration of Workout{" "}
              <input
                type="number"
                onChange={e => setDurationOfWorkout(e.target.value)}
                value={durationOfWorkout}
                placeholder="0 (minutes)"
              />
            </div>
            <h3>Set a Goal:</h3>
            <div>
              <input
                type="radio"
                name="goal"
                onChange={e => {
                  setGain(e.target.checked);
                  setLose(!e.target.checked);
                  setMaintain(!e.target.checked);
                }}
              />
              gain muscles/ bulk
            </div>
            <div>
              <input
                type="radio"
                name="goal"
                onChange={e => {
                  setGain(!e.target.checked);
                  setLose(e.target.checked);
                  setMaintain(!e.target.checked);
                }}
              />
              lose weight/ cut
            </div>
            <div>
              <input
                type="radio"
                name="goal"
                onChange={e => {
                  setGain(!e.target.checked);
                  setLose(!e.target.checked);
                  setMaintain(e.target.checked);
                }}
              />
              maintain your current weight
            </div>
            <h3>Choose a Diet:</h3>
            <div>
              <input
                type="radio"
                name="diet"
                onChange={e => {
                  setLowCarbs(e.target.checked);
                  setHighCarbs(!e.target.checked);
                  setModerateCarbs(!e.target.checked);
                }}
              />
              low-carbs
            </div>
            <div>
              <input
                type="radio"
                name="diet"
                onChange={e => {
                  setLowCarbs(!e.target.checked);
                  setHighCarbs(!e.target.checked);
                  setModerateCarbs(e.target.checked);
                }}
              />
              moderate-carbs
            </div>
            <div>
              <input
                type="radio"
                name="diet"
                onChange={e => {
                  setLowCarbs(!e.target.checked);
                  setHighCarbs(e.target.checked);
                  setModerateCarbs(!e.target.checked);
                }}
              />
              high-carbs
            </div>
          </div>
          <div style={{ width: "100%" }}>
            <button>Sign Up</button>
          </div>
        </form>
      </div>
    );
}
