import React, { useEffect, useState, useContext } from "react";
import { TokenContext } from "./TokenContext";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {

} from "@fortawesome/free-solid-svg-icons";

export default function Profile() {
  const [info, setInfo] = useState({
    name: "",
    TDEE: "",
    goal: "",
    protein: "",
    carbs: "",
    fat: "",
    sugar: "",
  });
  const [token, setToken] = useContext(TokenContext);
  useEffect(() => {
    fetch("http://localhost:5000/profile", {
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
    })
      .then((res) => res.json())
      .then((data) =>
        setInfo({
          name: data.username,
          TDEE: data.tdee,
          goal: data.goalCal,
          protein: data.protein,
          carbs: data.carbs,
          fat: data.fat,
          sugar: data.sugar,
        })
      );
  }, [token]);

  return (
    <div
      style={{
        // textShadow: '1px 1px #fff',
        backgroundColor: "#7dbf37",
        color: "#000",
        width: "100%",
        display: "flex",
        flexWrap: "wrap",
        justifyContent: "space-evenly",
        padding: "10px",
      }}
    >
      <div style={{ justifySelf: "flex-start", width: "300px" }}>
        Hey {info.name},
        <br /> Here's your daily need of calories and nutrition. <br /> have a
        healthy and wonderful day {":)"}
      </div>
      <div>
        <div style={{ textAlign: "center" }}>
          <span title="Total Daily Energy Expenditure">
            TDEE: <span>{info.TDEE}</span> kcal
          </span>
          <p>(your daily calories to maintain your current weight)</p>
        </div>
        <div>
          Goal Calories: <span>{info.goal}</span> kcal
        </div>
        <div>
          Protein: <span>{info.protein}</span> g
        </div>
        <div>
          Carbs: <span>{info.carbs}</span> g{" "}
        </div>
        <div>
          Fat: <span>{info.fat}</span> g{" "}
        </div>
        <div>Sugar: <span></span> g </div>
        <div>Caffein: 400-500 mg</div>
      </div>
    </div>
  );
}
