import React, { useState, useContext} from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import {TokenContext} from './TokenContext';
import "../styles/header.css";

import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faUtensils, faStar, faUserEdit, faSignOutAlt } from "@fortawesome/free-solid-svg-icons";
import {Link} from "react-router-dom";
import {
  Collapse,
  Navbar,
  NavbarToggler,
  NavbarBrand,
  Nav,
  NavItem,
  NavLink
} from "reactstrap";
import SearchBar from "./SearchBar";

const Header = () => {
  const [isOpen, setIsOpen] = useState(false);
 // const [query, setQuery] = useContext(ApiContext);

  const token = useContext(TokenContext);
  console.log(token);

  const toggle = () => setIsOpen(!isOpen);
  return (
    <div style={{padding: '0 40px 0 40px'}}>
      <Navbar color="light" light expand="md">
        <NavbarBrand style={{ paddingRight: "5%" }} href="/">
          Food for All
        </NavbarBrand>
        <NavbarToggler onClick={toggle} />
        <Collapse isOpen={isOpen} navbar>
          <Nav className="mr-auto, nav-bar" navbar>
            <NavItem>
              <Link to="/recipes">
                Recipes <FontAwesomeIcon icon={faUtensils} />
              </Link>
            </NavItem>
            <NavItem>
              <SearchBar />
              {/* <form style={{"display":"flex"}}>
                <Input type="text" name="search" className="input-search" placeholder="Search" onChange={updateQuery}/>
                <button>
                  <FontAwesome name="search"  />
                </button>
              </form> */}
            </NavItem>
            {token ? (
              <div
                style={{
                  width: "100px",
                  display: "flex",
                  justifyContent: "space-between"
                }}
              >
                <div></div>
                {" "}
                <FontAwesomeIcon icon={faStar} />{" "}
                <FontAwesomeIcon icon={faUserEdit} />{" "}
                <FontAwesomeIcon icon={faSignOutAlt} />
              </div>
            ) : (
              <div style={{ display: "flex" }}>
                <NavItem>
                  <NavLink href="/create-account">Create an account</NavLink>
                </NavItem>
                <NavItem>
                  <NavLink href="/login">Log in</NavLink>
                </NavItem>
              </div>
            )}
          </Nav>
        </Collapse>
      </Navbar>
    </div>
  );
};

export default Header;
