import React, { useState, useEffect, useContext } from "react";
import { useHistory } from "react-router-dom";
import { TokenContext } from "./TokenContext";

export default function EditAccount(props) {
  const history = useHistory();
  const [token, setToken] = useContext(TokenContext);
 useEffect(() => {
   console.log(token);
   fetch("http://localhost:5000/profile", {
     headers: {
       "Content-Type": "application/json",
       Authorization: `Bearer ${token}`,
     },
   })
     .then((res) => res.json())
     .then((data) =>
       setInfo({
         tdee: data.tdee,
         height: data.height,
         weight: data.weight,
         age: data.age,
         male: data.male,
         female: data.female,
         daysOfWorkout: data.daysOfWorkout,
         durationOfWorkout: data.durationOfWorkout,
         ecto: data.ecto,
         meso: data.meso,
         endo: data.endo,
         lose: data.lose,
         gain: data.gain,
         maintain: data.maintain,
         lowCarbs: data.lowCarbs,
         moderateCarbs: data.moderateCarbs,
         highCarbs: data.highCarbs,
       })
     );
 }, [token]);

  const [info, setInfo] = useState({
    tdee: "",
    height: "",
    weight: "",
    age: "",
    male: false,
    female: false,
    daysOfWorkout: 0,
    durationOfWorkout: 0,
    ecto: false,
    meso: false,
    endo: false,
    lose: false,
    gain: false,
    maintain: false,
    lowCarbs: false,
    moderateCarbs: false,
    highCarbs: false,
  });
  const [height, setHeight] = useState(info.height);
  const [weight, setWeight] = useState(info.weight);
  const [age, setAge] = useState(info.age);
  const [male, setMale] = useState(info.male);
  const [female, setFemale] = useState(info.female);
  const [ecto, setEcto] = useState(info.ecto);
  const [meso, setMeso] = useState(info.meso);
  const [endo, setEndo] = useState(info.endo);
  const [NEAT, setNEAT] = useState(0);
  const [gain, setGain] = useState(info.gain);
  const [lose, setLose] = useState(info.lose);
  const [maintain, setMaintain] = useState(info.maintain);
  const [goal, setGoal] = useState(0);
  const [lowCarbs, setLowCarbs] = useState(info.lowCarbs);
  const [moderateCarbs, setModerateCarbs] = useState(info.highCarbs);
  const [highCarbs, setHighCarbs] = useState(false);
  const [daysOfWorkout, setDaysOfWorkouts] = useState(info.daysOfWorkout);
  const [durationOfWorkout, setDurationOfWorkout] = useState(info.durationOfWorkout);
  const [index, setIndex] = useState("");


  const ratios = [
    {
      name: "high-carbs for bodybuilding",
      carbs: 50, // 40-60
      protein: 30, // 25-35
      fat: 20 // 15-25
    },
    {
      name: "moderate-carbs for maintenance",
      carbs: 40, // 30-50
      protein: 30, // 25-35
      fat: 30 // 25-35
    },
    {
      name: "low-carbs for reduction",
      carbs: 20, // 10-20
      protein: 50, // 40-50
      fat: 30 // 30-40
    }
  ];



  const handleSubmit = e => {
    e.preventDefault();
    let result =
      9.99 * parseFloat(weight) +
      6.25 * parseFloat(height) -
      4.92 * parseFloat(age);

    console.log(result);

    let BMR = Math.floor(male ? result + 5 : result - 161);
    console.log(BMR);
    let percentOfBMR = Math.floor((7 * BMR) / 100);

    let EPOC = parseFloat(daysOfWorkout) * percentOfBMR;
    console.log(EPOC);
    let TEA = Math.floor(
      (parseFloat(daysOfWorkout) * parseFloat(durationOfWorkout) * 9 + EPOC) / 7
    );
    console.log(TEA);
    let total = BMR + TEA + NEAT;
    console.log(total);
    let TEF = Math.floor(total / 10);

    let TDEE = total + TEF;

    let goalCal = TDEE + goal;

    let protein = Math.floor((TDEE * ratios[index].protein) / 100 / 4);
    let carbs = Math.floor((TDEE * ratios[index].carbs) / 100 / 4);
    let fat = Math.floor((TDEE * ratios[index].fat) / 100 / 9);
    let sugar;
    male ? (sugar = 37.5) : (sugar = 25);

    if (TDEE) {
      console.log(TDEE, goalCal, protein, carbs, fat, sugar);
      fetch("http://localhost:5000/edit-account", {
        method: "POST",
        body: JSON.stringify({
          tdee: TDEE,
          goal: goalCal,
          protein: protein,
          carbs: carbs,
          fat: fat,
          sugar: sugar,
          height: height,
          weight: weight,
          age: age,
          male: male,
          female: female,
          daysOfWorkout: daysOfWorkout,
          durationOfWorkout: durationOfWorkout,
          ecto: ecto,
          meso: meso,
