import React, {useEffect, useState, useContext} from 'react'
import { TokenContext } from './TokenContext'

export default function Profile() {
    // const [TDEE, setTDEE]=useState('');
    // const [goal, setGoal]=useState('');
    // const [protein, setProtein]=useState('');
    // const [fat, setFat]=useState('');
    // const [carbs, setCarbs]=useState('');
    // const [sugar, setSugar]=useState('');
    // const [name, setName]=useState('');
    const [info, setInfo]=useState({});
    const [token, setToken] = useContext(TokenContext);
    useEffect(()=>{
        fetch("http://localhost:5000/profile", {
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`
          }
        })
          .then(res => res.json())
          .then(data => setInfo({name: data.username,
          TDEE: data.TDEE,
        goal: data}));
    },[token])

    return (
        <div style={{width:'100%', display:'flex',flexWrap:'wrap', justifyContent:'space-evenly', padding:'4px'}}>
           <div style={{justifySelf:'flex-start', width: '300px'}}>Hello {name}!<br/> Here's your daily need of calories and nutrition. <br/> have a healthy and wonderful day {':)'}</div> 
           <div style={{textAlign:'center'}}><span title='Total Daily Energy Expenditure'>TDEE: {TDEE}kcal</span><p>your daily calories to maintain your current weight</p></div>
           <div>Goal Calories: {goal}kcal</div>
           <div>Protein: {protein}g</div>
           <div>Carbs: {carbs}g </div>
           <div>Fat: {fat}g </div>
           <div>Sugar: {sugar}g </div>
           <div>Caffein: 400-500mg</div>
        </div>
    )
}
