import React, { useEffect, useState, useContext } from "react";
import { TokenContext } from "./TokenContext";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
// import {

// } from "@fortawesome/free-solid-svg-icons";

export default function Profile() {
  const [info, setInfo] = useState({
    name: "",
    TDEE: "",
    goal: "",
    protein: "",
    carbs: "",
    fat: "",
    sugar: "",
  });
  const [token, setToken] = useContext(TokenContext);
  useEffect(() => {
    fetch("http://localhost:5000/profile", {
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
    })
      .then((res) => res.json())
      .then((data) =>
        setInfo({
          name: data.username,
          TDEE: data.tdee,
          goal: data.goalCal,
          protein: data.protein,
          carbs: data.carbs,
          fat: data.fat,
          sugar: data.sugar,
        })
      );
  }, [token]);

  return (
    <div
      style={{
        // textShadow: '1px 1px #fff',
        backgroundColor: "#b526f2",
        color: "#fff",
        width: "100%",

      }}
    >
      <div
        style={{
          display: "flex",
          justifyContent: "space-between",
          width: "90%",
        }}
      >
        <div style={{ padding: '20px 10px 20px 40px', width: "330px" }}>
          Hey {info.name},
          <br /> Here's your daily need of calories and nutrition. <br /> have a
          healthy and wonderful day {":)"}
        </div>
        <div
          style={{
            display: "flex",
            flexWrap: "wrap",
            padding:'5px',
            justifyContent: "space-evenly",
            width: '630px'
          }}
        >
          <div style={{ textAlign: "center" }}>
            <span title="Total Daily Energy Expenditure">
              <b>TDEE: </b>
            </span>{" "}
            <span style={{ color: "#000" }}>{info.TDEE}</span> kcal
            <p style={{ color: "#3d3b3b" }}>
              (your daily calories to maintain your current weight)
            </p>
          </div>
          <div>
            <b>Goal Calories: </b>
            <span style={{ color: "#000" }}>{info.goal}</span> kcal
          </div>
          <div>
            <b>Protein: </b>
            <span style={{ color: "#000" }}>{info.protein}</span> g
          </div>
          <div>
            <b>Carbs: </b>
            <span style={{ color: "#000" }}>{info.carbs}</span> g{" "}
          </div>
          <div>
            <b>Fat: </b>
            <span style={{ color: "#000" }}>{info.fat}</span> g{" "}
          </div>
          <div>
            <b>Sugar: </b>
            <span style={{ color: "#000" }}>{info.sugar}</span> g{" "}
          </div>
          <div>
            <b>Caffein: </b>
            <span style={{ color: "#000" }}>400-500</span> mg
          </div>
        </div>
      </div>
    </div>
  );
}
